import {Button, Form} from "react-bootstrap";
import {useState} from "react";

const Loggin = () => {
    const [name ,setName]=useState("")
    const [gender ,setGender]=useState("")
const submitFormHandler = () => {
        console.log(name , gender);
    }
  return(
      <div className="container mb-3 p-3 my-2">
          <Form>
              <fieldset >
                  <Form.Group className="mb-3">
                      <Form.Label htmlFor="disabledTextInput">نام</Form.Label>
                      <Form.Control id="" placeholder=" نام"  value={name} onChange={e=>setName(e.target.value)}/>
                  </Form.Group>
                  <Form.Group className="mb-3">
                      <Form.Label htmlFor="">جنسیت</Form.Label>
                      <Form.Select id=""  value={gender} onChange={e=> setGender(e.target.value)}>
                          <option>زن</option>
                          <option>مرد</option>
                      </Form.Select>
                  </Form.Group>

                  <Button onClick={submitFormHandler}>ثبت</Button>
              </fieldset>
          </Form>
      </div>
  )
}
export default Loggin
