import {Button, Form} from "react-bootstrap";
import {useState} from "react";

const Loggin = () => {
    const [name ,setName]=useState("")
    const [gender ,setGender]=useState("")
    const validtion = (user) => {
      if(!user.name )return "نام خود را وارد کنید"
        if(user.gender === "" ||user.gender === 0) return "جنسیت خود را وارد کنید"


    }
const submitFormHandler = () => {
        console.log(name , gender);
        const error = validtion({name, gender})
    if (error)
        document.getElementById("error").innerHTML = error;
    else
        document.getElementById("success").innerHTML = "ثبت شد";
    // document.getElementById("error").innerHTML = ""


}
  return(
      <div className="container mb-3 p-3 my-2 " dir="rtl" style={{ width: '35rem' }}>
          <Form>
              <fieldset >
                  <Form.Group className="mb-3">
                      <Form.Label htmlFor="disabledTextInput">نام</Form.Label>
                      <Form.Control id="" placeholder=" نام"  value={name} onChange={e=>setName(e.target.value)}/>
                  </Form.Group>
                  <Form.Group className="mb-3">
                      <Form.Label htmlFor="">جنسیت</Form.Label>
                      <Form.Select id=""  value={gender} onChange={e=> setGender(e.target.value)} placeholder="یک گزینه زا انتخاب کنید">
                          <option value={0}>یک گزینه زا انتخاب کنید</option>

                          <option value="woman">زن</option>
                          <option value="man">مرد</option>
                      </Form.Select>
                  </Form.Group>

                  <Button onClick={submitFormHandler} >ثبت</Button>
              </fieldset>
          </Form>
          <p className="alert-danger col-3 text-center container" id="error" ></p>
          <p className="alert-success col-3 text-center container" id="success"></p>
      </div>
  )
}
export default Loggin
