import {useEffect, useRef, useState} from "react";
import { Routes, Route, useLocation } from 'react-router-dom';
import { io } from "socket.io-client";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import './chatroom.css';
const ChatRoom = () => {
    let params = useLocation();
   const{ state : user}= params;
   const [messages , setMessages]=useState([])
const [newMessage , setNewMessage]=useState("")
    const socket=useRef(io.connect("localhost:3010/socket", {transports: ['polling']}))
    // eslint-disable-next-line react-hooks/rules-of-hooks
    useEffect(()=>{
        console.log(params.state)
        socket.current.on("newMessage",(message)=>{
            console.log(message)
            setMessages(messages=>messages.concat(message))
        })
    },[]);
const sendMessage=()=>{
    if (!newMessage)
        return;
    socket.current.emit("newMessage" , {
        id:"",
        msg:newMessage,
        sender:{
            name:params.state.name,
            gender: params.state.gender,
        }

    });
    setNewMessage("");
}
  return(

          <div className="page-content page-container" id="page-content">
              <div className="padding">
                  <div className="row container d-flex justify-content-center">
                      <div className="col-md-6">
                          <div className="card card-bordered">
                              <div className="card-header">
                                  <h4 className="card-title"><strong>Chat</strong></h4>
                              </div>


                              <div className="ps-container ps-theme-default ps-active-y"  id="chat-content">
                                  {
                                      messages.map(() => {
                                          return <div className="media media-chat">
                                          <img className="avatar"
                                               src="https://img.icons8.com/color/36/000000/administrator-male.png"
                                               alt="..."/>
                                          <div className="media-body">
                                              <p>{messages.msg}</p>

                                              <p className="meta">
                                                  <time dateTime="2018">23:58</time>
                                              </p>
                                          </div>
                                      </div>

                                  })
                                  }
                                  {/*<div className="media media-chat media-chat-reverse">*/}
                                  {/*    <div className="media-body">*/}
                                  {/*        <p>Hiii, I'm good.</p>*/}
                                  {/*        <p>How are you doing?</p>*/}
                                  {/*        <p>Long time no see! Tomorrow office. will be free on sunday.</p>*/}
                                  {/*        <p className="meta">*/}
                                  {/*            <time dateTime="2018">00:06</time>*/}
                                  {/*        </p>*/}
                                  {/*    </div>*/}
                                  {/*</div>*/}


                                  <div className="ps-scrollbar-x-rail" >
                                      <div className="ps-scrollbar-x" tabIndex="0" ></div>
                                  </div>
                                  <div className="ps-scrollbar-y-rail" >
                                      <div className="ps-scrollbar-y" tabIndex="0"></div>
                                  </div>
                              </div>

                              <div className="publisher bt-1 border-light">
                                  <img className="avatar avatar-xs"
                                       src="https://img.icons8.com/color/36/000000/administrator-male.png" alt="..."/>
                                      <input
                                          className="publisher-input"
                                          type="text"
                                          placeholder="Write something"
                                          value={newMessage}
                                          onChange={e=>setNewMessage(e.target.value)}
                                      />

                                          <button className="publisher-btn text-info"  onClick={sendMessage}><i className="fa fa-paper-plane"></i></button>
                              </div>

                          </div>
                      </div>
                  </div>
              </div>
          </div>

  )
}
export default ChatRoom
